			{
				echo "<option value='".$d['id_kelas']."'>".$d['nama_kelas']."</option>";
			}
			?>
		</select>
	</td></tr>
	<tr><td colspan="2">
		<input type="submit" name="submit" class="btn success block" value="Simpan">
	</td></tr>
