<?php
if($core->method_post('submit',false,true))
{
	$nama=$core->method_post('nama');
	$jurusan=$core->method_post('jurusan');
	$kelas=$core->method_post('kelas');

	$db->insert('data_siswa',['',$kelas,$jurusan,$nama]);
	$core->redirect('?p=crud_panicode&m=all');
}